import React from 'react'
import Header from "./Header";
import FavItem from './FavItem';

const PlayTitle= props => {

return(
    <div className="default"> 
    <Header />
            <h2> {props.title} </h2>
            <div>
                {props.synopsis}
            </div>
            
            <button>Close</button>
            <FavItem addFav={props.addFav}>
            <button> {"<3"} </button>
            </FavItem >
    </div>
  
)
}

export default PlayTitle;
