import React from 'react'
import Header from "./Header";
import Favourites from "./Favourites";
import PlayTitle from "./PlayTitle";
import Tabs from "./Tabs";


const PlayDetails = props => {

    const [fullPlaysList, setFullPlaysList] = useLocalStorage("plays", []);    
const url = "https:www.randyconnolly.com//funwebdev/3rd/api/shakespeare/play.php?name=" + props.id;
fetch(url)
.then(response => {
    if (response.ok){
        return response.json();
    } else {
        return (
            <div className="default">
        <Header/>
        <PlayTitle /> 
        <Tabs />
            </div>
        )
    }
})    
.then(data => {

})

return(
<div className="default">
        <Header/>
        <PlayTitle /> 
        <Tabs>
            <div label="Details">
                {props.details}
            </div>
            <div label="Characters">
                {props.characters}
            </div>
            <div label="Text">
                {props.text}
            </div>
        </Tabs>
</div>
  
)
}

export default PlayDetails;
